package com.xjk.nettyWebSocket.controller;import com.xjk.nettyWebSocket.entity.WebSocket;import com.xjk.nettyWebSocket.server.WebSocketServer;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import org.springframework.web.servlet.ModelAndView;@RestController()public class WebSocketController {    @Autowired    WebSocketServer webSocketServer;    /**     * test     * */    @GetMapping("/socket/{sid}")    public ModelAndView socket(@PathVariable String sid) {        ModelAndView mav = new ModelAndView("/socket");        mav.addObject("sid", sid);        return mav;    }    /**     * 建立socket的链接     *     * @param webSocket webSocket对象     */    @RequestMapping(value = "/socket/getWebSocket")    public String getWebSocket(@RequestBody WebSocket webSocket) {        return webSocketServer.addWebSocket(webSocket).toString();    }    @PostMapping("/socket/push")    public String push(@RequestBody WebSocket webSocket) {        webSocketServer.push(webSocket);        return "success";    }}